		subjects.Add(new Subject() { SeedIdentifier = "Gen2_2", Name = "Maturitní fyzika", Description = @"PROBÍHÁ NA MFF UK V ÚTERÝ RÁNO.\n\nMaturitní fyzika je určena pro studenty septimy a oktávy. Předmět je koncipován tak, aby se probíraná témata v průběhu dvou let neopakovala. Z hlediska tematického tedy předmět tvoří dvouletý blok.\nVýuka se zaměřuje na tři větší tematické okruhy:\n1.Moderní fyzika (1. rok bloku-relativistická fyzika, astrofyzika; 2. rok bloku-fyzika mikrosvěta, elektronika, fyzika pevných látek)\n2.Rozšíření základního učiva fyziky (témata rozšiřující učivo mechaniky, termodynamiky, optiky, elektřiny a magnetizmu, které bylo probíráno v hodinách fyziky v primě – sextě)\n3.Přednášky odborníků z různých pracovišť MFF UK a exkurze do laboratoří MFF UK.\n", Capacity = 25, ScheduleDayOfWeek = DayOfWeek.Monday, ScheduleSlotInDay = (ScheduleSlotInDay)1, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Drozd")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.HumanNature } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.GraduationSeminar });
		subjects.Add(new Subject() { SeedIdentifier = "Gen2_3", Name = "Maturitní biologie", Description = @"Organizace: Předmět je přednostně určen pro studenty, kteří uvažují o složení maturitní zkoušky z biologie.\n\nObsah semináře: Rozšiřující příprava k maturitní zkoušce a přijímacím zkouškám z biologie: důraz na ontogenezi a fylogenezi, dále genetiku, ekologii, vývoj života na Zemi\nProvázáno s praktickou prací s organismy (mikroskopování, příprava preparátů, pitvy, poznávačky)\n\nRealizace výuky: Pracovna biologie – chemie, exkurze, výukové programy, terén \n", Capacity = 25, ScheduleDayOfWeek = DayOfWeek.Tuesday, ScheduleSlotInDay = (ScheduleSlotInDay)1, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Jurčáková")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.HumanNature } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.GraduationSeminar });
		subjects.Add(new Subject() { SeedIdentifier = "Gen2_4", Name = "Maturitní chemie", Description = @"Organizace: Předmět je přednostně určen pro studenty, kteří uvažují o složení maturitní zkoušky z chemie.\n\nObsah semináře: Seminář je určený k procvičení a prohloubení znalostí chemie pro studenty, kteří by chtěli důkladně porozumět základním zákonům přírody. V rámci semináře se studenti pokusí nalézt odpovědi na základní otázky jak a proč probíhají chemické děje v živých i neživých systémech. Cílem bude i porozumění základním bezpečnostním opatřením jak pracovat s chemickými látkami, aniž bychom ublížili sobě nebo okolí. Studenti se naučí vyhledávat informace na internetu a aplikací kritického myšlení vybrat ty správné pro studovaný problém. Další cíl předmětu, je příprava zájemců na maturitní zkoušku z chemie a případné další studium na chemických a lékařských VŠ. K procvičování modelových příkladů k přijímacím zkouškám bude studentům přístupný e learningový systém pro samostudium. \nProbereme vybrané kapitoly z obecné a fyzikální chemie, anorganické chemie, organické chemie, průmyslové chemie, chemie životního prostředí a biochemie.\n\nDoplňující informace: Studenti budou používat ochranné pomůcky jako brýle (školní), plášť a rukavice (vlastní).\n\nRealizace výuky: Pracovna biologie – chemie", Capacity = 14, ScheduleDayOfWeek = DayOfWeek.Monday, ScheduleSlotInDay = (ScheduleSlotInDay)5, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Šebestík")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.HumanNature } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.GraduationSeminar });
		subjects.Add(new Subject() { SeedIdentifier = "Gen2_18", Name = "Maturitní dějepis", Description = @"Prohloubení dovedností a znalostí z oboru historie prostřednictvím vymezených tematických okruhů (chronologických či problémových), které zároveň sehrají roli maturitních okruhů v rámci profilové maturity. Diferencovaný postup u studentů septimy a oktávy. Důraz je kladen na samostatnou práci studentů, práci se sekundárními i primárními zdroji informací. Studenti semináře by měli lépe porozumět širším souvislostem historického vývoje, prohloubit schopnost identifikovat a analyzovat různé interpretace dějinných událostí i historických jevů, zároveň však také upevnit stávající a výrazně rozšířit o nové znalosti z oblasti faktografie. Měli by si osvojit rozsáhlou škálu znalostí a dovedností umožňující provádět kvalifikovanější hodnocení jevů v oblasti historie a příbuzných oborů (postoje).", Capacity = 25, ScheduleDayOfWeek = DayOfWeek.Monday, ScheduleSlotInDay = (ScheduleSlotInDay)1, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Brdek")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.HumanSociety } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.GraduationSeminar });
		subjects.Add(new Subject() { SeedIdentifier = "Gen2_19", Name = "Maturitní právo", Description = @"Seminář připravuje studenty na úspěšné složení maturitní zkoušky z práva. Jsou zde systematicky probrána a procvičována příslušná maturitní témata.", Capacity = 25, ScheduleDayOfWeek = DayOfWeek.Sunday, ScheduleSlotInDay = (ScheduleSlotInDay)4, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Sladká")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.HumanSociety } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.GraduationSeminar });
		subjects.Add(new Subject() { SeedIdentifier = "Gen2_21", Name = "Maturitní psychologie", Description = @"Seminář připravuje studenty na úspěšné složení maturitní zkoušky z psychologie. Jsou zde systematicky probrána a procvičována příslušná maturitní témata. V předmětu budou probírána i sociologická témata s příslušným přesahem do psychologie. Absolvent semináře získá celkový přehled v problematice hlavních psychologických disciplín.\n\nKritéria hodnocení: Studenti odevzdají ke každému čtvrtletí referát o rozsahu cca tří normostran, který bude na základě doporučené odborné literatury zpracovávat probíraná témata. Studenti, kteří si vyberou psychologii jako maturitní předmět, povinně nastudují pět odborných publikací a zpracují jejich anotaci. Cílem referátů je postupné zvládnutí práce s odbornou literaturou a rozvoj schopností jak kompilačně, tak kreativně zpracovávat psychologická témata. Dále bude sledována pravidelnost docházky a aktivita v rámci výuky, zejména zapojování se do diskuzí a schopnost vyjadřovat vlastní názory.", Capacity = 25, ScheduleDayOfWeek = DayOfWeek.Thursday, ScheduleSlotInDay = (ScheduleSlotInDay)3, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Hofmannová")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.HumanSociety } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.GraduationSeminar });
		subjects.Add(new Subject() { SeedIdentifier = "Gen2_22", Name = "Maturitní literatura", Description = @"Seminář je koncipován jako příprava k maturitní zkoušce z literatury. Cílem tohoto semináře je reflektovat vlastní četbu a systematicky interpretovat literární díla vybraná k maturitě. Jednotlivá díla rozebereme ve všech jejich vrstvách – tematické, kompoziční a jazykové. Dílo zasadíme do kontextu doby vzniku a tvorby autora. Součástí semináře je opakování literárních slohů a směrů, jejich charakteristických znaků a hlavních představitelů. Zaměříme se také na prohloubení učiva literární teorie s důrazem na její aplikaci při analýze textů. \n\nMetody a aktivity: četba textů, rozbory knih, ukázek filmů a divadelních představení, interpretace, analýza, srovnání, výklad, diskuze, metody tvořivé dramatiky.\n", Capacity = 25, ScheduleDayOfWeek = DayOfWeek.Tuesday, ScheduleSlotInDay = (ScheduleSlotInDay)1, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Veverová")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.LanguageCommunication } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.GraduationSeminar });
		subjects.Add(new Subject() { SeedIdentifier = "Gen2_23", Name = "Maturitní filozofie", Description = @"Obsah semináře: Seminář připravuje studenty na úspěšné složení maturitní zkoušky z daného oboru Je určen pro žáky s hlubším zájmem o rozvoj celosvětové kultury, lidských duchovních dějin a pokroku myšlení; pro žáky, kteří se zamýšlejí nad filosofickými, náboženskými a mytickými problémy, rádi čtou, tvoří svůj vlastní pohled na otázky života jednotlivce a společnosti; prostě dokážou se rozvoji lidského ducha věnovat nejen v povinných hodinách.", Capacity = 25, ScheduleDayOfWeek = DayOfWeek.Tuesday, ScheduleSlotInDay = (ScheduleSlotInDay)4, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Jankovec")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.HumanSociety } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.GraduationSeminar });
		subjects.Add(new Subject() { SeedIdentifier = "Gen2_24", Name = "Maturitní politologie (praktická politologie)", Description = @"Studenti dostanou příležitost zapojit se do interaktivního výběrového semináře, ve kterém se konfrontuje teorie s praktickou aplikací – současný stav světa.\n\nMetoda práce – výklad spojený s diskusí. Každý student zpracuje a přednese jedno téma. Během výuky bude organizována strategická hra. Jejím smyslem je hodnotit schopnost studenta zvládnout přidělenou roli, správně se rozhodnout.\n\nObsahově se seminář soustředí na níže uvedená témata:\nČím se nechává společnost vést. Moc, autorita a panství.\nVhodné a pokleslé formy vlády v dějinách lidstva a dnes. \nDeformace moci (korupce, nepotismus, klientelismus, renta)\nStátní moc, její výkon, dělba státní moci. Kdo nese odpovědnost vůči komu. \nLegalita a legitimita výkonu moci. Vztah stát – občan (jedinec).\nIdeologie, které dnes ovládají politickou scénu, čím se liší, jak je rozeznat v praxi.\nPolitické strany a hnutí, jaké jsou dnes na politické scéně, co je pro ně charakteristické.\nMezinárodní právo veřejné, co je pro něj charakteristické, prameny mezinárodního práva.\nSvětová centra moci, jejich soupeření. Světový řád. Konec dějin nebo návrat k vestfálskému uspořádání.\nCo nás ohrožuje (migrace, terorismus, pandemie.)\nVálka světa – kdo s kým válčí s jakým motivem.\nZákladní mezinárodní organizace, jejich náplň a poslání, rozdíl mezi klasickými mezinárodními organizacemi a Evropskou unií.", Capacity = 25, ScheduleDayOfWeek = DayOfWeek.Thursday, ScheduleSlotInDay = (ScheduleSlotInDay)3, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Svoboda")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.HumanSociety } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.GraduationSeminar });
		subjects.Add(new Subject() { SeedIdentifier = "Gen2_25", Name = "Maturitní ekonomie", Description = @"Cílem předmětu „Maturitní ekonomie „je seznámit žáky s vybranými ekonomickými otázkami, které jsou nezbytné pro pochopení základních souvislostí ve fungování naší společnosti. Jedná se o více méně vyčerpávající výklad všech ekonomických otázek nejen z hlediska moderní ekonomické teorie, ale i o historický přehled a rozbor ekonomických směrů a škol. Hlavní probíranou tématikou je vysvětlení základních ekonomických pojmů, principů, zákonů a struktury ekonomické vědy. Žáci se seznámí se zákonitostmi chování spotřebitele, firmy, státu a zahraničního obchodu. Do učiva budou zahrnuty i vybrané problémy vývoje ekonomických teorií a hospodářské politiky. Ze všech otázek budou vybrány pouze podstatné poznatky, které umožní žákovi vytvořit svůj vlastní názor na fungování ekonomiky. Předmět „Maturitní ekonomie“ je určen pro žáky s hlubším zájmem o rozvoj nejen celosvětového, ale především českého hospodářství. Ve výuce se počítá s diskuzemi, výměnou polemických názorů; v podstatě výuka bude probíhat formou usměrňované diskuze, kdy každý žák bude mít možnost posoudit předkládané ekonomické tvrzení a vyjádřit svůj vlastní názor. Výuka je určena pro žáky, kteří rádi čtou, tvoří svůj vlastní pohled na ekonomické otázky; prostě dokáží se rozvoji současného hospodářství věnovat nejen v povinných hodinách. Ve výuce se seznámí s originálními texty ekonomů různých směrů v podobě knižní i elektronické. Najdou v nich potřebné informace o formování ekonomie jako vědy a budou mít možnost s materiály nejen pracovat, ale i vyjádřit svůj vlastní názor na ně. V průběhu výuky se pokusíme vyřešit složitější problémy moderní ekonomiky. Žáci si přečtou náročnější texty českého ekonomického myšlení, budou diskutovat o postavách českých hospodářských dějin. Totéž zkusí i opačně. Vytvoří vlastní krátké referáty, respektive eseje o tom či onom zajímavém ekonomovi či směru. Studium daného předmětu může být chápán jako základ pro absolvování vysoké školy příslušného oboru.", Capacity = 25, ScheduleDayOfWeek = DayOfWeek.Tuesday, ScheduleSlotInDay = (ScheduleSlotInDay)5, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Bedretdinov")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.HumanSociety } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.GraduationSeminar });
		subjects.Add(new Subject() { SeedIdentifier = "Gen2_26", Name = "Maturitní sociologie", Description = @"Předmět představuje studentům sociologii jako teoreticko-empirickou vědní disciplínu. V teoretické části se zaměřuje na vlivné sociologické teorie od 19. století po současnost. Pozornost je věnována základním sociologickým pojmům a aplikaci sociologie na některé oblasti života společnosti.\nV metodologické části se studenti učí základům obecné metodologie sociologického výzkumu, s důrazem na metody a techniky kvantitativního výzkumu, zejména na dotazníkové šetření. Výuka se zaměřuje na formulování výzkumného problému, volbu odpovídající výzkumné strategie a nástroje sběru dat. Studenti jsou vedeni také k přípravě vlastního výzkumného projektu.", Capacity = 25, ScheduleDayOfWeek = DayOfWeek.Thursday, ScheduleSlotInDay = (ScheduleSlotInDay)4, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Hampl")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.HumanSociety } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.GraduationSeminar });
		subjects.Add(new Subject() { SeedIdentifier = "Gen2_27", Name = "Maturitní zemepis", Description = @"Seminář navazuje na vyučovací předmět zeměpis a nadstavbový historicko-geografický seminář, prohlubuje a zároveň syntetizuje jednotlivá témata. \nStudenti budou mít možnost si v rámci semináře procvičovat již osvojené znalosti a dovednosti. Dalším ze základních cílů předmětu, úzce spojeným s těmi předchozími, je příprava zájemců na maturitní zkoušku ze zeměpisu a případné další studium geografických oborů na VŠ.\n\nDnes lze studovat celou řadu geografických či aplikovaných oborů, některé pro představu uvádím:\nGeografie (i učitelství), Demografie, Geologie, Aplikovaná geologie, Kartografie a geoinformatika, Regionální a politická geografie, Sociální geografie a regionální rozvoj, Geografie a anglistika – amerikanistika, Demografie s ekonomií, Demografie se sociální geografií, Fyzická geografie a geologie, Mezinárodní vztahy, Geografie a kartografie, Ochrana životního prostředí, Ochrana a tvorba krajiny", Capacity = 25, ScheduleDayOfWeek = DayOfWeek.Tuesday, ScheduleSlotInDay = (ScheduleSlotInDay)4, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Kulhánek")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.HumanNature } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.GraduationSeminar });
		subjects.Add(new Subject() { SeedIdentifier = "Gen2_28", Name = "Maturitní matematika", Description = @"Obsah semináře: Cílem semináře je připravit studenty na úroveň znalostí nutných ke splnění maturitní zkoušky na školní i státní úrovni. Obsahem semináře bude tedy doplnění středoškolského učiva a jeho rozšíření. Náplní bude analytická geometrie v prostoru, kuželosečky a aplikace analytické geometrie v lineární algebře (práce s vektory a maticemi). V druhé polovině roku bude náplní semináře soustavná příprava na maturitní zkoušku a opakování jednotlivých maturitních okruhů pokrývajících celé středoškolské studium matematiky. \n\nKritéria hodnocení: Hodnocena bude domácí příprava na hodinu (zpracované MO), pravidelné písemné práce (téměř každou hodinu), v druhém pololetí pak řešení obdobných testů k dané maturitní zkoušce.", Capacity = 25, ScheduleDayOfWeek = DayOfWeek.Tuesday, ScheduleSlotInDay = (ScheduleSlotInDay)5, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Makešová")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.MathApplication } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.GraduationSeminar });
		subjects.Add(new Subject() { SeedIdentifier = "Gen2_29", Name = "Nadstavbový seminár z fyziky", Description = @"PROBÍHÁ NA MFF UK V ÚTERÝ ODPOLEDNE.\n\nPředmět slouží k prohloubení kompetencí a znalostí z fyziky, umožňuje výraznější propojení znalostí a dovedností z tohoto oboru s dalšími obory. Významnou složkou výuky budou i fyzikální experimenty.", Capacity = 25, ScheduleDayOfWeek = DayOfWeek.Monday, ScheduleSlotInDay = (ScheduleSlotInDay)4, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Drozd")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.HumanNature } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Kvinta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Sexta }, }, CategoryId = (int)SubjectCategory.Entry.ExtensionSeminar });
