		subjects.Add(new Subject() { SeedIdentifier = "Gen2_34", Name = "Seminář z matematiky (rozšiřující seminář z matematiky)", Description = @"Obsah semináře: Cílem Semináře z matematiky je připravit zájemce o studium na VŠ technického typu. Obsahem semináře bude tedy rozšíření středoškolského studia o znalosti nutné k absolvování zmíněných škol. Seminář započne úvodem do důkazů a jejich užití, dále se zaměří na komplexní čísla a jejich využití v praxi. V druhé polovině roku bude náplní semináře diferenciální počet – úvod do limit funkcí, derivací a integrálů.\nKritéria hodnocení: Hodnocena bude aktivita, písemné práce, domácí příprava.", Capacity = 25, ScheduleDayOfWeek = DayOfWeek.Sunday, ScheduleSlotInDay = (ScheduleSlotInDay)1, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Makešová")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.MathApplication } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.GraduationSeminar });
