		subjects.Add(new Subject() { SeedIdentifier = "Gen2_37", Name = "Seminář práce s operačním systémem Linux", Description = @"Hlavním cílem předmětu je:\n·seznámit se s ekosystémem operačního systému Linux,\n·naučit se pohybovat po souborovém systému, pracovat s procesy,\n·proniknout do psaní skriptů (programování) v shellu,\n·nainstalovat si vlastní virtuální stroj,\n·spravovat uživatele a software, naučit se základům síťování,\n·nainstalovat a nakonfigurovat webový server,\n·vytvořit statické webové stránky v HTML/CSS,\n·zinteraktivnit webové stránky pomocí programování CGI a PHP.\nKritéria hodnocení: aktivita na semináři, plnění zadaných úkolů, 2x pololetní test.\n\nDALŠÍ DOPLŇUJÍCÍ INFORMACE:\nPředpoklady: schopnost ovládání počítače, práce na internetu.\n\nPomůcky: žádné, resp. může být vlastní počítač; studijní materiály autorské, on-line.· \n", Capacity = 12, ScheduleDayOfWeek = DayOfWeek.Monday, ScheduleSlotInDay = (ScheduleSlotInDay)5, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Ivo Šír")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.Informatics } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Tercie }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Kvarta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Kvinta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Sexta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.SpecialisationSeminar });
		subjects.Add(new Subject() { SeedIdentifier = "Gen2_38", Name = "Arduino a programování", Description = @"Účast by bylo vhodné prodiskutovat s vyučujícím.\n\nSeminář je určený pro studenty, kteří si chtějí postavit a zprovoznit vlastní zařízení na platformě Arduino. Studenti si osvojí konstrukci vlastních elektrických obvodů a jejich řízení pomocí mikroprocesorů. Studenti se naučí programovat tyto obvody tak, aby dokázali reagovat ne pozorovaný signál a jeho intenzitu. V rámci předmětu se pokusíme sestavit a oživit přístroje vhodné pro měření běžných fyzikálně-chemických veličin. Pokusíme se např. o automatický bodotávek (zařízení k určení bodu tání látek), automatický titrátor (zařízení pro sledování nebo řízení neutralizačních reakcí), vlastní řízený elektrolyzér (příprava různých plynů, kovů, kyselin, bází nebo solí), vlastní spektrometr pro měření spekter látek (umožní třeba stanovit množství známé látky v roztoku nebo porozumět složení hvězd), vlastní polarimetr pro měření otáčivosti cukrů, vlastní fotoreaktor (zařízení pro reakce vyžadující světelnou energii).\n\nDoplňující informace: Vhodné je, aby se zájemci orientovali ve fyzice a chemii. Výuka bude v počítačové učebně (programovací část) a občas v učebně BiChe (testovací část).", Capacity = 12, ScheduleDayOfWeek = DayOfWeek.Monday, ScheduleSlotInDay = (ScheduleSlotInDay)4, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Šebestík")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.HumanNature } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Kvinta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Sexta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.SpecialisationSeminar });
