		subjects.Add(new Subject() { SeedIdentifier = "Gen2_41", Name = "Moderní dějiny a další vybraná témata dějin", Description = @"Seminář bude zaměřen především na podrobnější rozbor politických a sociálních dějin po Francouzské revoluci, občas bude zařazeno téma i ze starších dějin (pokud tato problematika výrazně ovlivňuje pozdější vývoj). Zvláštní důraz bude kladen na české a světové dějiny 20. století. Tedy období, jehož události výrazně ovlivnily, nebo stále ovlivňují dnešní svět. V rámci semináře se soustředíme rovněž na přípravu na Dějepisnou soutěž gymnázií. Velký důraz bude kladen na práci s filmovým materiálem, audiozáznamy, obrazovými prameny nejrůznějších typů, primárními i sekundárními prameny. Tematickou skladbu semináře z části ovlivní i náměty studentů.", Capacity = 25, ScheduleDayOfWeek = DayOfWeek.Thursday, ScheduleSlotInDay = (ScheduleSlotInDay)4, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Hanzl")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.HumanSociety } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Kvinta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Sexta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.SpecialisationSeminar });
