		subjects.Add(new Subject() { SeedIdentifier = "Gen2_47", Name = "Seminář z výtvarné výchovy 1", Description = @"POZOR! Je možné přihlásit se pouze na jeden seminář z Výtvarné výchovy. Můžete si zvolit termín dle rozvrhu jiných předmětů buď od 14:30, nebo 16:15.\n\nSeminář Výtvarná výchova 1 je zaměřen na tvorbu uplatňující neomezené množství přístupů od tradičních technik po avantgardní a alternativní přístupy. Tento seminář je určen pro studenty, kteří mají zájem o výtvarnou tvorbu, a hravou formou si chtějí rozšířit své výtvarné možnosti a schopnosti (řemeslnou zručnost). Seznámíme se s novými výtvarnými technikami, budeme experimentovat nebo jen tak tvořit. Součástí semináře budou také probíhat výtvarné víkendové dílny a další zajímavé projekty.\n\nZákladní kritéria hodnocení v předmětu:\nŽáci jsou hodnoceni podle vyžadované přípravy na hodinu a vlastní aktivity v hodinách. Součástí této aktivity je relativní míra a kvalita komunikace (dílem, o díle a nad dílem), relativní míra a snaha najít a zvládnout řešení problému, relativní kvalita a snaha o prezentaci vlastního názoru na problém a jeho řešení. Pokud studen zamešká více než 40 % bude automaticky nehodnocen (výjimka se toleruje u dlouhodobé nemoci, nebo jiných závažných důvodů, které žák s učitelem osobně prokonzultuje).", Capacity = 20, ScheduleDayOfWeek = DayOfWeek.Tuesday, ScheduleSlotInDay = (ScheduleSlotInDay)4, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Herchlová")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.ArtCulture } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Sekunda }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Tercie }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Kvarta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Kvinta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Sexta }, new SubjectGradeRelatio