		subjects.Add(new Subject() { SeedIdentifier = "Gen2_50", Name = "Počítačová grafika 1", Description = @"Stručná charakteristika předmětu: \nPráce v semináři je zaměřena na různé formy v oblasti počítačové grafiky. Práce v semináři vyplývá podle zkušeností studenta, takže je velmi individuální. Na konkrétních úkolech se studenti naučí chápat principy grafického designu, propagačního výtvarnictví a seznámí se s grafickými editory (gimp, inkscape, photoshop, ilustrator ...) V jednotlivých zadáních pak budou moc realizovat vlastní tvůrčí nápady a rozvíjet tak svůj osobitý výtvarný projev. Část semináře je zaměřena na digitální fotografii a animovaný film (technika stop motion). \nZákladní kritéria hodnocení v předmětu: \nŽáci jsou hodnoceni podle vyžadované přípravy na hodinu a vlastní práce v hodinách. Další hodnocení je za relativní míru pokroku, tvořivosti, originality, snahy a úspěšnosti v řešení zadaných problémů a v reflexi vlastních a cizích výsledků práce. Pokud student zamešká více než 40%, bude automaticky nehodnocen (výjimka se toleruje u dlouhodobé nemoci, nebo jiných závažných důvodů, které žák s učitelem osobně konzultuje).", Capacity = 15, ScheduleDayOfWeek = DayOfWeek.Tuesday, ScheduleSlotInDay = (ScheduleSlotInDay)4, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Dojčiaková")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.ArtCulture } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Sekunda }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Tercie }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Kvarta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Kvinta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Sexta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.En