		subjects.Add(new Subject() { SeedIdentifier = "Gen2_51", Name = "Poslech hudby", Description = @"Podtitul:\nPoslech hudby v historickém kontextu - skladby z různých slohových období od renesance po současnost v souladu či kontrastu k zažitým ideálům či dobovým charakteristikám.\n\nObsah semináře: V tomto semináři se budeme hlavně věnovat dílům slavných i méně známých autorů, kteří jsou představiteli různých uměleckých slohů či stylů od renesance až po současnost. V hodinách hudební výchovy není vždy prostor na to, abychom si vychutnali skladby od začátku do konce a mohli si tak udělat ucelenější představu např. o hudebních formách a jejich vývoji. Není také dostatek času představit si široký záběr některých skladatelů a uvědomit si tak např. rozmanitost námětů či stylový vývoj v jejich tvorbě.\n\nSeminář není primárně zaměřen na historická fakta, nebude obsahovat testy z letopočtů či souhrnného díla vybraných skladatelů. Hodnotit budu zájem o předmět, aktivní účast v diskuzích a každý student si během roku připraví prezentaci na zadané téma (po domluvě). Během školního roku také navštívíme nějaké hudební představení (koncert, opera) a výstavu či muzeum s hudební tematikou.", Capacity = 12, ScheduleDayOfWeek = DayOfWeek.Monday, ScheduleSlotInDay = (ScheduleSlotInDay)4, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Kubačák")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.ArtCulture } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.SpecialisationSeminar });
