		subjects.Add(new Subject() { SeedIdentifier = "Gen2_77", Name = "Mediální seminář", Description = @"Obsah:\nInteraktivní kurs, kde se vše odehrává formou her a tréninků. Jak fungují noviny a proč zanikají? Proč servery ukazují polooblečené ženy? Sestavíme vlastní Home Page. Proč politici nenávidí novináře a proč novináři nerozumí politikům? Jak minimalizovat mediální škody, když jsme udělali chybu? Jak odhalit hoax a vytvořit vlastní. Chcete být hvězdou sociálních sítí?\nJak psát dobře, i když je to nezáživné. Mail. Životopis. Seminární práci. Jak po sobě editovat text. Tvůrčí psaní – komentář, fejeton, povídka… Všechno vyzkoušíme a budeme o tom diskutovat. Nula teorie, samá praxe.\nLektor:\nJmenuji se Komárek. Celý život se zabývám psaním. Je to jediné, co umím a občas slyším, že dobře. (Ostatně to vím, nač falešná skromnost). Skoro třicet let jsem byl hlavním komentátorem kdysi velkého deníku MF DNES. V současnosti jsem šéfkomentátorem Deníku. Spolupracoval jsem s televizí i rozhlasem, psal do časopisů. Večer od psaní odpočívám psaním. Vydal jsem přes dvacet knížek, jak beletrii, tak literaturu faktu. Od Dřevěné panenky a Mefitis v 90. letech až po Poslední revoluci předloni. Hlásím se i k dobrodružné epizodě ve sněmovně v letech 2014-2017.", Capacity = 25, ScheduleDayOfWeek = DayOfWeek.Tuesday, ScheduleSlotInDay = (ScheduleSlotInDay)4, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Komárek")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.ArtCulture }, new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.LanguageCommunication } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Kvarta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Kvinta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Sexta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.SpecialisationSeminar });
		subjects.Add(new Subject() { SeedIdentifier = "Gen2_78", Name = "Psychopatologie", Description = @"Obsah a cíle semináře: seznámit studenty s různými pojetími normy a nenormy jak v historii, tak v současnosti, s vývojovým náhledem na duševní nemoci, základními příčinami, průběhem, léčením a možnostmi prevence základních typů duševních nemocí. Kurz by měl studentům umožnit základní orientaci v různých typech duševních poruch, lépe tak detekovat možné psychické potíže u sebe, či okolí, rozeznat, kdy je zapotřebí vyhledat pomoc a která forma pomoci by mohla být nejvhodnější.\n\nZÁKLADNÍ TÉMATA:\n1.VZTAH NORMY A NENORMY – jejich proměny historicky a společensky, dnešní kritéria normy…\n2.HISTORICKÝ NÁHLED NA PŘÍČINY DUŠEVNÍCH ONEMOCNĚNÍ – vývoj možností léčení psychických potíží – cesta od magie k psychoterapii, srovnání východního a západního pohledu na psychické potíže…\n3.NEJNOVĚJŠÍ POZNATKY O MOZKU VZHLEDEM K PSYCHICE – současné diskuze o vztahu mozku a vědomí…\n4.RIZIKOVÁ ŽIVOTNÍ OBDOBÍ PRO VZNIK DUŠEVNÍCH POTÍŽÍ – dospívání a mladá dospělost, krize středního věku, krize rodinné, osobní a společenské…\n5.ZÁKLADNÍ PŘÍČINY VZNIKU DUŠEVNÍCH POTÍŽÍ – genetické, somatické, psychologické a společenské…\n6.ZÁKLADNÍ PORUCHY PSYCHICKÝCH FUNKCÍ – vnímání, paměť, myšlení, inteligence, pudy…\n7.TYPOLOGIE NEUROTICKÝCH ONEMOCNĚNÍ – generalizovaná úzkostná porucha, smíšená úzkostně depresivní porucha, obsedantně – kompulzivní porucha, fobická porucha…\n8.DUŠEVNÍ ONEMOCNĚNÍ PSYCHOTICKÉHO CHARAKTERU – bipolární afektivní porucha, schizofrenie, paranoidní psychóza…\n9. ZÁKLADNÍ PORUCHY OSOBNOSTI – hraniční, impulzivní, schizoidní, histriónská, narcistická, anankastická…\n10. TYPOLOGIE ZÁVISLOSTÍ – alkohol, drogy, hráčství, počítače, internet….\n11. ZÁKLADNÍ MOŽNOSTI LÉČENÍ DUŠEVNÍCH POTÍŽÍ – psychiatrické, psychologické a jiné přístupy.", Capacity = 25, ScheduleDayOfWeek = DayOfWeek.Tuesday, ScheduleSlotInDay = (ScheduleSlotInDay)4, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Hofmannová")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.HumanSociety } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Kvinta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Sexta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.SpecialisationSeminar });
		subjects.Add(new Subject() { SeedIdentifier = "Gen2_79", Name = "Business english language course", Description = @"Outline: You will learn important new words and phrases associated with the world of Business. Read authentic articles from the financial times and other newspapers and books on business. Develop essential business communication skills e.g. making presentations, negotiating and taking part in meetings.\n\nDescription: This course focuses on the world of Business and learning Business English with relevant and challenging communicative activities. Each unit covers a topical theme, such as Brands or Money, and introduces a functional business skill, like 'Opening a Meeting' or 'Giving a Sales Pitch'. The case studies at the end of each unit tie everything together. You will be given the opportunity to practise your skills in various exercises based on realistic business problems or situations, and allow you to use the language and communication skills you have developed while working through the unit. Some units, you will instead present your work for your unit evaluation instead.", Capacity = 12, ScheduleDayOfWeek = DayOfWeek.Tuesday, ScheduleSlotInDay = (ScheduleSlotInDay)4, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Silva")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.LanguageCommunication } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Sexta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.SpecialisationSeminar });
		subjects.Add(new Subject() { SeedIdentifier = "Gen2_80", Name = "Seminář k dějinám, teorii a praxi divadla a filmu", Description = @"Obsah semináře: Seminář představí základní etapy a některé nejvýznamnější představitele dějin evropského divadla, a to s ohledem na postupnou proměnu dramatu od kulticko-náboženského rituálu až po rozpad iluzivního divadla ve prospěch moderní nejednoznačnosti charakterů a postmoderní meta-narativnosti příběhu. V druhé části roku seminář představí i vybrané kapitoly a osobnosti dějin světového kinematografie. Součástí budou filmové ukázky divadelních představení a filmových děl. Seminář navíc poukáže i na základní koncepce herectví a prakticko-technické základy audiovizuální tvorby. Klasifikaci bude možné vázat například na výstupy typu: prezentace koncepce malého divadelního či filmového projektu, rozbor divadelního či filmového díla, realizace audio či video-rozhovoru žurnalistického ladění, filmová reportáž, filmová scénka či výtvarně pojaté audiovizuálního dílo.\nNástin osnovy semináře:\n-Zrod divadla z náboženského rituálu a mýtu, antické drama a jeho hlavní představitelé, vymezení tragédie a komedie s ohledem na Aristotelovu Poetiku. \n-Role divadla v křesťanském středověku: smysl mystéria, mirácula a morality versus fraška a pastorála.\n-Vznik renesančního divadla z ducha humanismu: Italská commedie dell´arte, Alžbětínské divadlo, W. Shakespeare.\n-Barokní a klasicistní drama jako návrat k antice a výraz racionalistické filosofie: J. Racin, P. Corneille, Molière. \n-Pre-romantismus jako reakce na aristokratismus klasicismu a omezenost racionalismu a romanismus jako výraz svobody a uměleckého génia: osobnost J. W. Goethe a F. Schillerova filosofická koncepce hry jako cesty k plnému životu a ideální společnosti. \n-Nástup realismu a naturalismu jako důsledek zvědečtění existence a industrializace světa: H. Ibsen, Čechov \n-Rozpad iluzivního divadla a jednoznačnosti heroických postav jako důsledek rozpadu tradičního řádu světa.\n-Revoluce avantgardního umění na počátku 20. století: dadaismus, surrealismus, symbolismus \n-Divadlo a jeho dvojenec: divadelní „teorie“ A. Artaud. \n-Epické divadlo B. Brechta: estetický význam principu zcizení.\n-Poválečná deziluze: existenciální drama J. Sartra a A. Camuse.\n-Absurdní drama: E. Ionesco, S. Beckett, V. Havel.\n-Principy teorie herectví u K. S. Stanislavskiho. Dialogické jednání s vnitřním partnerem jako herecká a tvůrčí metoda spisovatele a dramatika I. Vyskočila.\n-Počátky kinematografie: dokumentární zachycení skutečnosti – bratři Lumiérové. Vynález filmového triku a divadelní prostředky ve filmu – iluzionista a filmový mág G. Méliès. \n-Filmový střih a filmová montáž – zrod velké kinematografie: D. W. Griffith jako průkopník Hollywoodu. \n-Ruská montážní škola, filmový surrealismus, německý expresionismus a úspěch F. Langa v Americe.\n-Meziválečný film v Československu. \n-Film a filmový průmysl jako nástroj politické propagandy: film v době druhé světové války.\n-Italský neorealismus. Velikáni italského filmu: L. Visconti, F. Fellini, M. Antonioni.\n-Nové vlny v Evropě. Francouzská nová vlna a fenomén J. L. Godard.\n-Československá nová vlna: M. Forman, V. Chytilová, Juráček a další.\n-Americký nezávislý film.\n-Hlavní filmové profese: režie, dramaturgie a scénář, kreativní a exekutivní producent. \n-Praktické základy audiovizuální tvorby: kamera, střih, smysl námětu a scénáře. \n\nStručné představení vyučujícího: Boris Jankovec je absolventem estetiky na FF UK v Praze, externě spolupracoval například s Českým rozhlasem a Českou televizí. Momentálně pracuje na disertaci na Katedře autorské tvorby a pedagogiky na Divadelní akademii múzických umění v Praze.", Capacity = 25, ScheduleDayOfWeek = DayOfWeek.Tuesday, ScheduleSlotInDay = (ScheduleSlotInDay)5, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Jankovec")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.ArtCulture }, new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.HumanSociety } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Kvinta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Sexta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.SpecialisationSeminar });
		subjects.Add(new Subject() { SeedIdentifier = "Gen2_81", Name = "Konverzace v AJ pro méně pokročilé", Description = @"Seminář není opravdu určen pro studenty více pokročilé v AJ.\nObsah semináře: Seminář je koncipovaný jako podpůrný konverzační seminář pro žáky, kteří si v angličtině nepřipadají příliš jistí, rádi by se zlepšili a ztratili ostych v komunikaci. V semináři bychom se zaměřili na zlepšení výslovnosti, rozšíření slovní zásoby a získání jistoty v mluveném projevu. Obzvláště pokud se bojíte mluvit, cítíte, že často tápete po tom správném slovíčku, máte potíže se „vymáčknout“, tak je tento seminář pro vás jako stvořený. Rozmluvíme se pomocí různých her, skupinových aktivit či divadla.\n\nŽáci budou hodnoceni na základě aktivity a mluveného projevu (výstupy v rámci hodin, předpřipravená témata z domova apod.). Seminář je určen především pro žáky druhých skupin a méně jisté žáky ze skupin prvních. Pokud váháte, zda je pro vás seminář vhodný, ozvěte se.", Capacity = 15, ScheduleDayOfWeek = DayOfWeek.Monday, ScheduleSlotInDay = (ScheduleSlotInDay)4, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Polláková")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.LanguageCommunication } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Sekunda }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Tercie }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Kvarta }, }, CategoryId = (int)SubjectCategory.Entry.SpecialisationSeminar });
		subjects.Add(new Subject() { SeedIdentifier = "Gen2_82", Name = "Vybrané kapitoly ze starších dějin – dějiny slovem i obrazem", Description = @"Obsah předmětu: Seminář zaměřený na evropské dějiny od počátku středověku (s přesahy do starověku). Detailněji se seznámíme s některými aspekty historického vývoje daných období, na něž při běžné výuce obvykle nezbývá čas. Pozornost bude ovšem věnována i globálnímu pohledu s cílem pochopit jednotlivá historická období jako celky se všemi jejich specifiky. Ve velké míře se bude pracovat s obrazovým materiálem.\nKritéria hodnocení: Hodnocení bude vycházet z práce v hodinách, plnění zadaných pracovních úkolů (prezentace, eseje, zprávy, anotace, individuální i skupinové projekty aj…), písemných testů prověřujících především kompetence (badatelské úkoly) a dále znalost základní faktografie.", Capacity = 25, ScheduleDayOfWeek = DayOfWeek.Tuesday, ScheduleSlotInDay = (ScheduleSlotInDay)4, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Brdek")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.HumanSociety } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Sekunda }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Tercie }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Kvarta }, }, CategoryId = (int)SubjectCategory.Entry.SpecialisationSeminar });
		subjects.Add(new Subject() { SeedIdentifier = "Gen2_83", Name = "Seminář debatování", Description = @"obsah semináře: Důraz v semináři bude kladen na systematickou přípravu na debatní soutěže, mohou se ho ale účastnit i studenti, kteří zatím v debatování soutěžit nechtějí a chtějí se s debatováním jen seznámit a vyzkoušet si ho. \nCílem semináře je rozvoj následujících dovedností: \n•argumentace a strategie sestavování argumentační linie \n•kritický rozbor kontroverzních témat\n•diskuze nad důležitými společenskými tématy\n•práce s daty, se zdroji a statistikami \n•kultivovanost a přesvědčivost slovního projevu, projev před publikem bez trémy\n•rozvoj konkrétních dovedností potřebných pro soutěžní debatu (shrnovací řeč, negace argumentu) aj.", Capacity = 25, ScheduleDayOfWeek = DayOfWeek.Monday, ScheduleSlotInDay = (ScheduleSlotInDay)4, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Lacinová")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.HumanWork } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Sekunda }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Tercie }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Kvarta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Kvinta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Sexta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.SpecialisationSeminar });
		subjects.Add(new Subject() { SeedIdentifier = "Gen2_84", Name = "Sociální vědy v praxi", Description = @"(STUDIUM NA VYSOKÉ, ANEB JAK JSEM SE NAUČIL NEDĚLAT SI STAROSTI S KREDITY A MÍT RÁD ČETBU)\n\nObsah semináře: Cílem předmětu je poskytnout studentům základní balík znalostí a dovedností usnadňujících orientaci ve vysokoškolském prostředí.\nTematicky se, kromě představení samotného vysokoškolského sytému, budeme věnovat tématům jako je kauzalita a korelace, metodologie, příprava vědeckého výzkumu. Diskutovaná témata se budou vyučovat na příkladech z aktuálního dění a historických událostech. Předmět je zaměřen primárně na studenty se zájmem o budoucí vysokoškolské studium v sociálních vědách, avšak vzhledem k probíraným tématům bude přínosný i pro všechny, koho zajímá aktuální dění. Předmět bude hodnocen primárně na základě aktivity na hodinách a dále pak dle zpracování kratších domácích úkolů. \nVe zkratce: budeme si povídat o tom, jak vypadá dnešní univerzitní svět a o tom, proč se v novinách občas píšou „blbosti“, a jak to rozpoznat. 😊\nCíle předmětu\nAbsolventky a absolventi by si měli z předmětu odnést:\n•praktické znalosti ohledně akademického života, které by jim měly pomoci při výběru vysoké školy a v prvních měsících studia,\n•schopnost kriticky čist a analyzovat texty týkající se mezinárodního dění a domácí politiky z pohledu sociálních věd,\n•znalost základních principy vědecké práce v sociálních vědách a schopnost analyzovat aktuální události vědeckou optikou.\nForma výuky: V první části hodiny bude zpracovávaná látka nejprve teoreticky přednesena, v druhé části poté studenti budou pracovat s tématem skrze připravené aktivity. V rámci výuky se bude často pracovat s multimediálními nástroji, ať už se jedná o Youtube, Google Forms, nebo třeba Rstudio. Domácí příprava, pokud bude vyžadována, bude formou krátké četby, nebo shlédnutí videa a přípravy poznámek. Předmět bude primárně hodnocen na základě aktivity studentů v hodinách.\n\nStručné představení vyučujícího: Kryštof Kučmáš je zaměstnanec Univerzity Karlovy, Fakulty Sociálních Věd, kde se již několikátým rokem podílí na aplikovaném výzkumu v oboru bezpečnostních studií a zajišťuje komunikaci a administrativu Pražského centra pro výzkum míru (www. https://www.prcprague.cz/). Po studiu na Mensa gymnáziu absolvoval magisterský obor Bezpečnostní Studia na Katedře Politologie FSV, v rámci svých studií strávil semestry v Bruselu a v Kodani. Výzkumně se specializuje na práci s daty a význam expertízy ve společnosti. Ve volném čase se rád věnuje cestování na Balkán, modelářství a hraní počítačových her.", Capacity = 25, ScheduleDayOfWeek = DayOfWeek.Monday, ScheduleSlotInDay = (ScheduleSlotInDay)4, TeacherRelations = { new SubjectTeacherRelation() { TeacherId = users.First(t => t.Name.Contains("Kučmáš")).TeacherId.Value } }, TypeRelations = { new SubjectTypeRelation() { SubjectTypeId = (int)SubjectType.Entry.HumanSociety } }, GradeRelations = { new SubjectGradeRelation() { GradeId = (int)GradeEntry.Kvinta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Sexta }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Septima }, new SubjectGradeRelation() { GradeId = (int)GradeEntry.Oktava }, }, CategoryId = (int)SubjectCategory.Entry.SpecialisationSeminar });
